import { connect } from 'react-redux';
import React from 'react';
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';

class Calculadora extends React.Component {
  render() {
    return (
      <View style={styles.container}>
        <View style={styles.visor}>
            <Text>{this.props.countValue}</Text>
            <View style={styles.lastVal}>
                <Text>{this.props.lastVal}</Text>
            </View>
        </View>
        <View>
            <TouchableOpacity style={styles.button} onPress={()=>this.props.clickValue(1)}>
                <Text>1</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.button} onPress={()=>this.props.clickValue(1)}>
                <Text>2</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.button} onPress={()=>this.props.clickValue(1)}>
                <Text>3</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.button} onPress={()=>this.props.clickValue(1)}>
                <Text>+</Text>
            </TouchableOpacity>
        </View>
        <View>

        </View>
        <View>

        </View>
        <View>

        </View>
      </View>
    )
  }
}

const styles = StyleSheet.create({
    root: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        marginTop: '20vh'
    },
    container: {
        borderRadius: 5
    },
    visor: {

    },
    button: {

    },
    lastVal: {
    }
});

function mapStateToProps(state){
    return {
        countValue: state.value,
        lastVal: state.lastValue === null ? 'None' : state.lastValue
    };
}

function mapDispatchToProps(dispatch) {
    return {
        clickValue: function(value){
        return dispatch({ type:"updateValue", value:value })
        },
        clear: function(){
        return dispatch({ type:"clear" })
        },
        operate: function(operation){
        return dispatch({ type: "operation", operation: operation })
        },
        equalsTo: function(){
        return dispatch({ type: "equalsTo" })
        }
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(Calculadora);
